#include "header.h"

    // already have a string array of size 1000
    // pointer is pointing to the very last digit of the string
    // while pointer >= string start address
    // convert char into an int and store in current
    // current = current*2 + carry
    // carry = current/10
    // current = current%10;
    // store current into the pointer
    // pointer--

// outputs a string with the value of 2^n
void twoPower(int n) {
    char myString[] = "";
    
    char* pointer = myString;
    int pointerIndex = 0;
    int current, carry = 0;
    
    int power = n;
    
    while(power > 0) {
        
        pointer += strlen(myString)-1;
        
        while(pointerIndex < 1000-1) {
            current = *pointer - '0';
            current = current*2 + carry;
            
            carry = current/10;
            
            current = current%10;
            
            *pointer = current + '0';
            
            pointer--;
            pointerIndex++;
        }
        
        pointerIndex = 0;
        power--;
    }
    
    printf("my string: %s", myString);
}

/*
 This is what I used as an input for sumString()...
 
 char myString[] = "";
 */

// method that takes in a string (of integers) and outputs the sum of the individual digits
int sumString(char myString[]) {
    char* pointer = myString;
    int sum = 0;
    
    for(int i=0; i<strlen(myString); i++) {
        sum += *pointer - '0';
        
        pointer++;
    }
    
    return sum;
}




